// pages/HomePage.js
import React, { useState, useEffect } from 'react';import styled from 'styled-components';
import BlogPreview from '../components/BlogPreview';

const HomeContainer = styled.div`
  text-align: center;
  padding: 2rem;
  background: linear-gradient(to bottom, #6c63ff, #3f3d56);
  color: white;
`;

const Title = styled.h1`
  font-size: 3rem;
`;

const Subtitle = styled.h2`
  font-size: 2rem;
  margin: 1rem 0;
`;

const Introduction = styled.p`
  font-size: 1.2rem;
  max-width: 600px;
  margin: 0 auto;
`;

const HomePage = () => {
    const [posts, setPosts] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
  
    useEffect(() => {
      // Fetch posts from an API or use static posts
      // Assuming posts are already defined as an array
      const filteredPosts = posts.filter(post =>
        post.title.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setPosts(filteredPosts);
    }, [searchTerm]);
  
    return (
      <BlogContainer>
        <h2>Latest Blog Posts</h2>
        <input
          type="text"
          placeholder="Search posts..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        {posts.map(post => (
          <BlogCard key={post.id}>
            <Link to={`/post/${post.id}`} style={{ textDecoration: 'none', color: 'inherit' }}>
              <BlogTitle>{post.title}</BlogTitle>
              <p>{post.content.substring(0, 100)}...</p>
            </Link>
          </BlogCard>
        ))}
      </BlogContainer>
    );
  };
export default HomePage;
