// pages/BlogPost.js
import React from 'react';
import styled from 'styled-components';
import { useParams } from 'react-router-dom';

const BlogPostContainer = styled.section`
  padding: 2rem;
  max-width: 800px;
  margin: auto;
`;

const Title = styled.h1`
  font-size: 2.5rem;
  color: #6c63ff;
  text-align: center;
`;

const Content = styled.div`
  margin-top: 1rem;
  line-height: 1.6;
  font-size: 1.1rem;
  color: #333;
`;

const BlogPost = () => {
  const { id } = useParams();
  const posts = [
    { id: 1, title: 'Understanding JavaScript Closures', content: 'Full article about closures...' },
    { id: 2, title: 'Styling Components in React', content: 'Full article about styling...' },
    { id: 3, title: 'Building Responsive Layouts', content: 'Full article about responsive design...' },
  ];

  const post = posts.find(post => post.id === parseInt(id));

  return (
    <BlogPostContainer>
      {post ? (
        <>
          <Title>{post.title}</Title>
          <Content>{post.content}</Content>
        </>
      ) : (
        <p>Post not found!</p>
      )}
    </BlogPostContainer>
  );
};

export default BlogPost;
