// components/BlogPreview.js
import React, { useEffect,} from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';

const BlogContainer = styled.section`
  padding: 2rem;
  background: #282c34;
  color: white;
`;

const BlogCard = styled.div`
  background: #333;
  border-radius: 8px;
  padding: 1rem;
  margin-bottom: 1rem;
  transition: transform 0.3s;
  &:hover {
    transform: scale(1.02);
  }
`;

const BlogTitle = styled.h3`
  color: #6c63ff;
`;

const BlogPreview = () => {
  const posts = [
    { id: 1, title: 'Understanding JavaScript Closures', content: 'A deep dive into closures...' },
    { id: 2, title: 'Styling Components in React', content: 'How to use Styled Components...' },
    { id: 3, title: 'Building Responsive Layouts', content: 'Techniques for responsive design...' },
  ];

  return (
    <BlogContainer>
      <h2>Latest Blog Posts</h2>
      {posts.map(post => (
        <BlogCard key={post.id}>
          <Link to={`/post/${post.id}`} style={{ textDecoration: 'none', color: 'inherit' }}>
            <BlogTitle>{post.title}</BlogTitle>
            <p>{post.content}</p>
          </Link>
        </BlogCard>
      ))}
    </BlogContainer>
  );
};

export default BlogPreview;
