// pages/HomePage.js
import React from 'react';
import styled from 'styled-components';
import BlogPreview from '../components/BlogPreview';

const HomeContainer = styled.div`
  text-align: center;
  padding: 2rem;
  background: linear-gradient(to bottom, #6c63ff, #3f3d56);
  color: white;
`;

const Title = styled.h1`
  font-size: 3rem;
`;

const Subtitle = styled.h2`
  font-size: 2rem;
  margin: 1rem 0;
`;

const Introduction = styled.p`
  font-size: 1.2rem;
  max-width: 600px;
  margin: 0 auto;
`;

const HomePage = () => {
  return (
    <HomeContainer>
      <Title>Welcome to My Blog!</Title>
      <Subtitle>Your go-to resource for Web Development</Subtitle>
      <Introduction>
        Here, you'll find articles about frontend development, React, JavaScript, and much more.
        Join me on this journey to learn and grow in the world of web technologies!
      </Introduction>
      <BlogPreview />
    </HomeContainer>
  );
};

export default HomePage;
