// pages/BlogPost.js
import React from 'react';
import styled from 'styled-components';
import { Prism as SyntaxHighlighter } from 'react-syntax-highlighter';
import { darcula } from 'react-syntax-highlighter/dist/esm/styles/prism';

const BlogPostContainer = styled.section`
  padding: 2rem;
  max-width: 800px;
  margin: auto;
`;

const Title = styled.h1`
  font-size: 2.5rem;
  color: #6c63ff;
  text-align: center;
`;

const PostContent = styled.div`
  margin-top: 1rem;
  line-height: 1.6;
  font-size: 1.1rem;
  color: #333;
`;

const CodeBlock = ({ language, children }) => (
  <SyntaxHighlighter language={language} style={darcula}>
    {children}
  </SyntaxHighlighter>
);

const RelatedPosts = styled.div`
  margin-top: 2rem;
  padding: 1rem;
  background: #f4f4f9;
  border-radius: 8px;
`;

const BlogPost = () => (
  <BlogPostContainer>
    <Title>Understanding JavaScript Closures</Title>
    <PostContent>
      <p>JavaScript closures are...</p>
      
      <h2>Code Example:</h2>
      <CodeBlock language="javascript">
        {`function outer() {
  let count = 0;
  return function inner() {
    count++;
    return count;
  };
}`}
      </CodeBlock>
      
      <p>Closures allow functions to...</p>
    </PostContent>
    
    <RelatedPosts>
      <h3>Related Posts</h3>
      <p>- How JavaScript Scopes Work</p>
      <p>- Mastering Higher-Order Functions</p>
    </RelatedPosts>
  </BlogPostContainer>
);

export default BlogPost;
