// pages/BlogPost.js
import React from 'react';
import styled from 'styled-components';
import { useParams, useNavigate } from 'react-router-dom';

const BlogPostContainer = styled.section`
  padding: 2rem;
  max-width: 800px;
  margin: auto;
`;

const Title = styled.h1`
  font-size: 2.5rem;
  color: #6c63ff;
  text-align: center;
`;

const Content = styled.div`
  margin-top: 1rem;
  line-height: 1.6;
  font-size: 1.1rem;
  color: #333;
`;

const GoBackButton = styled.button`
  margin: 2rem 0;
  padding: 0.5rem 1rem;
  border: none;
  border-radius: 5px;
  background: #6c63ff;
  color: white;
  cursor: pointer;
  font-size: 1rem;
  &:hover {
    background: #5757d6;
  }
`;

const BlogPost = () => {
  const { id } = useParams();
  const navigate = useNavigate(); // Get the navigate function

  const posts = [
    { id: 1, title: 'Understanding JavaScript Closures', content: 'Full article about closures...' },
    { id: 2, title: 'Styling Components in React', content: 'Full article about styling...' },
    { id: 3, title: 'Building Responsive Layouts', content: 'Full article about responsive design...' },
  ];

  const post = posts.find(post => post.id === parseInt(id));

  return (
    <BlogPostContainer>
      {post ? (
        <>
          <Title>{post.title}</Title>
          <Content>{post.content}</Content>
          <GoBackButton onClick={() => navigate('/')}>Go Back</GoBackButton>
        </>
      ) : (
        <p>Post not found!</p>
      )}
    </BlogPostContainer>
  );
};

export default BlogPost;
